import React, { Component } from 'react';
import api from "../../services/api";
import { Link } from 'react-router-dom'

import "./style.css"

import { render } from '@testing-library/react';

export default class Main extends Component {
    state = {
        classes: [],
        productInfo: {},
        page: 1,
    }


    componentDidMount() {
        this.loadProducts();
    }

    loadProducts = async (page = 1) => {
        const response = await api.get(`/classes?page=${page}`)

        const { docs, ...productInfo} = response.data;

        this.setState({ classes: docs, productInfo, page});
    };

    prevPage = () => {
        const { page, productInfo } = this.state;

        if (page == 1) return;

        const pageNumber = page - 1;

        this.loadProducts(pageNumber);
    }

    nextPage = () => {
        const { page, productInfo } = this.state;

        if (page == productInfo.pages) return;

        const pageNumber = page + 1;

        this.loadProducts(pageNumber);
    };


    render() {
        const { classe, page, productInfo } = this.state;

       return (
           <div className="product-list">
               {classe.map(classes => (
                   <article key={classes._id}>
                       <strong>{classes.title}</strong>
                       <p>{classes.description}</p>

                       <Link to={`/classes/${classes._id}`}>Acessar</Link>
                   </article>
               ))}
               <div className="actions">
                   <button disabled={page == 1} onClick={this.prevPage}>Anterior</button>
                   <button disabled={page == productInfo.pages} onClick={this.nextPage}>Pr√≥ximo</button>
               </div>
           </div>
       );
    }
}
